example.code:
for (int i = 0; i < 100; i++) {
  float r = randomGaussian();
  println(r);
}

for (int i = 0; i < 100; i++) {
  float length = randomGaussian() * 15;
  length = constrain(length, -50, 50);
  line(50, i, 50 + length, i);
}

int[] distribution = new int[100];

for (int i = 0; i < 100; ++i) {
  distribution[i] = int(randomGaussian() * 15) + 10;
}

translate(width / 2, width / 2);

for (int i = 0; i < 60; ++i) {
  rotate(radians(i));
  if (abs(distribution[i]) < 25) {
    stroke(255);
  } else {
    stroke(0);
  }
  line(0, 0, distribution[i], 0);
}
